    private boolean reloadHomeFragment(){

        networkInfo = connectivityManager.getActiveNetworkInfo();

        if(networkInfo!=null && networkInfo.isConnected()){

            MainActivity.drawerLayout.setDrawerLockMode(DrawerLayout.LOCK_MODE_UNLOCKED);

            categoryList.clear();
            homeList.clear();

            retryBtn.setVisibility(View.GONE);
            noInternetImage.setVisibility(View.GONE);

            categoriesRV.setVisibility(View.VISIBLE);
            scrollableTv.setVisibility(View.VISIBLE);
            homeFragmentRV.setVisibility(View.VISIBLE);

            setCategoriesDataFromFirebase();
            setHomeFragmentDataFromFirebase();

            if(swipeRefreshLayout.isRefreshing()){
                swipeRefreshLayout.setRefreshing(false);
            }

            return true;

        }
        else{

            MainActivity.drawerLayout.setDrawerLockMode(DrawerLayout.LOCK_MODE_LOCKED_CLOSED);

            retryBtn.setVisibility(View.VISIBLE);
            noInternetImage.setVisibility(View.VISIBLE);

            categoriesRV.setVisibility(View.GONE);
            scrollableTv.setVisibility(View.GONE);
            homeFragmentRV.setVisibility(View.GONE);

            if(swipeRefreshLayout.isRefreshing()){
                swipeRefreshLayout.setRefreshing(false);
            }

            return false;

        }

    }

    private void setCategoriesDataFromFirebase(){
        firestore.collection("CATEGORIES").orderBy("index").get().addOnCompleteListener(new OnCompleteListener<QuerySnapshot>() {
            @Override
            public void onComplete(@NonNull Task<QuerySnapshot> task) {
                if(task.isSuccessful()){
                    for(DocumentSnapshot shot : task.getResult()){
                        String name = (String) shot.get("category_name");
                        String icon_url = (String) shot.get("category_icon");
                        categoryList.add(new CategoriesModel(name,icon_url));

                    }

                    adapter = new CategoriesAdapter(categoryList);
                    categoriesRV.setAdapter(adapter);
                    adapter.notifyDataSetChanged();
                }
                else {
                    Toast.makeText(getContext(), ""+task.getException().getMessage(), Toast.LENGTH_SHORT).show();
                }

            }
        });
    }

    private void setHomeFragmentDataFromFirebase(){

        firestore.collection("CATEGORIES").document("HOME").collection("TODISPLAY").orderBy("index").get().addOnCompleteListener(new OnCompleteListener<QuerySnapshot>() {
            @Override
            public void onComplete(@NonNull Task<QuerySnapshot> task) {
                if(task.isSuccessful()){

                    for (DocumentSnapshot shot : task.getResult()){

                        long layoutType = (long) shot.get("layout_type");

                        if(layoutType == BANNER){

                            List<String> poster_img = (List<String>) shot.get("images_list");
                            homeList.add(new HomePageModel(BANNER,poster_img));

                        }
                        else if(layoutType == HORIZONTAL){

                            String layoutTitle = (String) shot.get("layout_title");
                            List<String> product_list = (List<String>) shot.get("product_list");

                            homeList.add(new HomePageModel(HORIZONTAL,product_list,layoutTitle));

                        }
                        else if(layoutType == GRID){

                            String layoutTitle = (String) shot.get("layout_title");
                            List<String> product_list = (List<String>) shot.get("product_list");
                            String bgColor = (String) shot.get("bg_color");

                            homeList.add(new HomePageModel(GRID,product_list,layoutTitle,bgColor));

                        }
                        else {

                        }


                    }

                    //call the adapter

                    homeAdapter = new HomePageAdapter(homeList);
                    homeFragmentRV.setAdapter(homeAdapter);
                    homeAdapter.notifyDataSetChanged();

                    if(swipeRefreshLayout.isRefreshing()){
                        swipeRefreshLayout.setRefreshing(false);
                    }

                    loadingDialog.dismiss();
                }
                else {
                    Toast.makeText(getContext(), ""+task.getException().getMessage(), Toast.LENGTH_SHORT).show();
                    loadingDialog.dismiss();
                }
            }
        });


    }
